import { useQuery } from "@tanstack/react-query";
import Navbar from "@/components/ui/navbar";
import Footer from "@/components/ui/footer";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { 
  Key, 
  Copy, 
  CheckCircle, 
  XCircle, 
  Clock, 
  Info,
  Shield,
  AlertTriangle,
  Calendar,
  Activity
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { useState } from "react";

interface VendorApiKey {
  id: number;
  keyName: string;
  apiKey: string;
  isActive: boolean;
  createdAt: string;
  lastUsed: string | null;
  expiresAt: string | null;
  rateLimit: number;
  description: string | null;
}

export default function VendorApiKeys() {
  const { toast } = useToast();
  const [copiedKeyId, setCopiedKeyId] = useState<number | null>(null);

  const { data: apiKeys, isLoading } = useQuery<VendorApiKey[]>({
    queryKey: ['/api/vendor/api-keys'],
  });

  const copyToClipboard = (text: string, keyId: number) => {
    navigator.clipboard.writeText(text);
    setCopiedKeyId(keyId);
    toast({
      title: "Copied to clipboard",
      description: "API key has been copied successfully",
    });
    setTimeout(() => setCopiedKeyId(null), 2000);
  };

  const formatDate = (dateString: string | null) => {
    if (!dateString) return "Never";
    return new Date(dateString).toLocaleString('en-IN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const isExpired = (expiresAt: string | null) => {
    if (!expiresAt) return false;
    return new Date(expiresAt) < new Date();
  };

  return (
    <div className="min-h-screen bg-background">
      <Navbar />
      
      <div className="container mx-auto px-4 py-8 max-w-7xl">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-foreground mb-2 flex items-center gap-2">
            <Key className="h-8 w-8 text-primary" />
            API Keys
          </h1>
          <p className="text-muted-foreground">
            Manage your API keys for third-party POS integration
          </p>
        </div>

        <Alert className="mb-6 border-primary/50 bg-primary/5">
          <Info className="h-4 w-4" />
          <AlertDescription>
            <strong>Important:</strong> API keys are generated by administrators. If you need an API key for your POS system integration, please contact support or request one through your admin dashboard.
          </AlertDescription>
        </Alert>

        {isLoading ? (
          <Card>
            <CardContent className="p-12 text-center">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4" />
              <p className="text-muted-foreground">Loading API keys...</p>
            </CardContent>
          </Card>
        ) : !apiKeys || apiKeys.length === 0 ? (
          <Card>
            <CardContent className="p-12 text-center">
              <Key className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-xl font-semibold text-foreground mb-2">
                No API Keys Found
              </h3>
              <p className="text-muted-foreground mb-6">
                You don't have any API keys yet. Contact your administrator to generate one.
              </p>
            </CardContent>
          </Card>
        ) : (
          <div className="space-y-4">
            {apiKeys.map((key) => (
              <Card key={key.id} className="overflow-hidden">
                <CardHeader className="bg-gradient-to-r from-primary/5 to-primary/10 border-b">
                  <div className="flex items-start justify-between">
                    <div className="flex items-center gap-3">
                      <div className="p-2 bg-primary/10 rounded-lg">
                        <Key className="h-5 w-5 text-primary" />
                      </div>
                      <div>
                        <CardTitle className="text-lg">{key.keyName}</CardTitle>
                        {key.description && (
                          <p className="text-sm text-muted-foreground mt-1">
                            {key.description}
                          </p>
                        )}
                      </div>
                    </div>
                    <div className="flex items-center gap-2">
                      {isExpired(key.expiresAt) ? (
                        <Badge variant="destructive" className="flex items-center gap-1">
                          <XCircle className="h-3 w-3" />
                          Expired
                        </Badge>
                      ) : key.isActive ? (
                        <Badge variant="default" className="flex items-center gap-1 bg-green-500">
                          <CheckCircle className="h-3 w-3" />
                          Active
                        </Badge>
                      ) : (
                        <Badge variant="secondary" className="flex items-center gap-1">
                          <XCircle className="h-3 w-3" />
                          Inactive
                        </Badge>
                      )}
                    </div>
                  </div>
                </CardHeader>
                <CardContent className="p-6 space-y-4">
                  <div className="space-y-3">
                    <div>
                      <label className="text-sm font-medium text-muted-foreground flex items-center gap-2 mb-2">
                        <Shield className="h-4 w-4" />
                        API Key
                      </label>
                      <div className="flex items-center gap-2">
                        <code className="flex-1 bg-muted px-4 py-2 rounded-md font-mono text-sm break-all">
                          {key.apiKey}
                        </code>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => copyToClipboard(key.apiKey, key.id)}
                          data-testid={`button-copy-apikey-${key.id}`}
                        >
                          {copiedKeyId === key.id ? (
                            <CheckCircle className="h-4 w-4 text-green-500" />
                          ) : (
                            <Copy className="h-4 w-4" />
                          )}
                        </Button>
                      </div>
                      <p className="text-xs text-muted-foreground mt-2">
                        <AlertTriangle className="h-3 w-3 inline mr-1" />
                        Keep this key secure. Never share it publicly or commit it to version control.
                      </p>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 pt-4 border-t">
                      <div>
                        <label className="text-sm font-medium text-muted-foreground flex items-center gap-2 mb-1">
                          <Calendar className="h-4 w-4" />
                          Created
                        </label>
                        <p className="text-sm text-foreground" data-testid={`text-created-${key.id}`}>
                          {formatDate(key.createdAt)}
                        </p>
                      </div>

                      <div>
                        <label className="text-sm font-medium text-muted-foreground flex items-center gap-2 mb-1">
                          <Activity className="h-4 w-4" />
                          Last Used
                        </label>
                        <p className="text-sm text-foreground" data-testid={`text-lastused-${key.id}`}>
                          {formatDate(key.lastUsed)}
                        </p>
                      </div>

                      <div>
                        <label className="text-sm font-medium text-muted-foreground flex items-center gap-2 mb-1">
                          <Clock className="h-4 w-4" />
                          {key.expiresAt ? "Expires" : "Expiration"}
                        </label>
                        <p className={`text-sm ${isExpired(key.expiresAt) ? 'text-destructive font-medium' : 'text-foreground'}`} data-testid={`text-expires-${key.id}`}>
                          {key.expiresAt ? formatDate(key.expiresAt) : "Never"}
                        </p>
                      </div>
                    </div>

                    {key.rateLimit && (
                      <div className="pt-2">
                        <label className="text-sm font-medium text-muted-foreground mb-1 block">
                          Rate Limit
                        </label>
                        <p className="text-sm text-foreground">
                          {key.rateLimit.toLocaleString()} requests per hour
                        </p>
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        <Card className="mt-6 border-blue-500/50 bg-blue-500/5">
          <CardHeader>
            <CardTitle className="text-lg flex items-center gap-2">
              <Info className="h-5 w-5 text-blue-600" />
              Integration Guide
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-3 text-sm">
            <div>
              <strong className="text-foreground">Using Your API Key:</strong>
              <ul className="mt-2 space-y-1 list-disc list-inside text-muted-foreground">
                <li>Include your API key in the Authorization header of your API requests</li>
                <li>Format: <code className="bg-muted px-2 py-1 rounded">Authorization: Bearer YOUR_API_KEY</code></li>
                <li>Use HTTPS for all API requests to keep your key secure</li>
                <li>Monitor your rate limits to avoid service interruptions</li>
              </ul>
            </div>
            <div>
              <strong className="text-foreground">Need Help?</strong>
              <p className="mt-1 text-muted-foreground">
                Contact support for API documentation, integration assistance, or to generate additional keys.
              </p>
            </div>
          </CardContent>
        </Card>
      </div>

      <Footer />
    </div>
  );
}
